import React, { useEffect, useRef } from 'react';
import './App.css'; // Import your main CSS file

function App() {
  const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;

    // Function to start audio playback after a delay
    const startAudio = () => {
      audio.loop = true;
      audio.muted = false; // Unmute after user interaction
      audio.play()
        .then(() => {
          console.log('Audio started successfully.');
        })
        .catch(error => {
          console.error('Failed to start audio:', error);
        });
    };

    // Delay start of audio by 1 second after component mounts
    const timeout = setTimeout(() => {
      startAudio();
    }, 1000); // 1000 milliseconds = 1 second

    // Clean up timeout to prevent memory leaks
    return () => clearTimeout(timeout);
  }, []);

  return (
    <div className="App">
      <main className="App-main">
        <div className="hamster-row">
          <img src="/images/anin.gif" alt="Animated Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/gerbil.gif" alt="Gerbil Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamu.gif" alt="Dancing Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamwalk.gif" alt="Walking Hamster" />
        </div>
      </main>
      <audio ref={audioRef} src="/music/originaldedodedo.wav" />
    </div>
  );
}

export default App;
