import React, { useEffect, useRef } from 'react';
import './App.css'; // Import your main CSS file

function App() {
  const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;
    audio.loop = true;
    audio.muted = true; // Start muted

    const playPromise = audio.play();

    if (playPromise !== undefined) {
      playPromise.then(_ => {
        // Autoplay started!
        audio.muted = false; // Unmute after user interaction
      }).catch(error => {
        // Autoplay was prevented
        console.error('Autoplay was prevented:', error);
      });
    }
  }, []);

  return (
    <div className="App">
      <main className="App-main">
        <div className="hamster-row">
          <img src="/images/anin.gif" alt="Animated Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/gerbil.gif" alt="Gerbil Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamu.gif" alt="Dancing Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamwalk.gif" alt="Walking Hamster" />
        </div>
      </main>
      <audio ref={audioRef} src="/music/originaldedodedo.wav" />
    </div>
  );
}

export default App;
