import React, { useState, useRef } from 'react';
import './App.css'; // Import your main CSS file

function App() {
  const [isAudioPlaying, setIsAudioPlaying] = useState(false);
  const audioRef = useRef(null);

  const handleStartAudio = () => {
    const audio = audioRef.current;
    audio.loop = true;
    audio.muted = false; // Unmute after user interaction
    audio.play();
    setIsAudioPlaying(true);
  };

  return (
    <div className="App">
      <main className="App-main">
        <div className="hamster-row">
          <img src="/images/anin.gif" alt="Animated Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/gerbil.gif" alt="Gerbil Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamu.gif" alt="Dancing Hamster" />
        </div>
        <div className="hamster-row">
          <img src="/images/hamwalk.gif" alt="Walking Hamster" />
        </div>
      </main>
      {!isAudioPlaying && (
        <button onClick={handleStartAudio} className="start-audio-button">
          Start Audio
        </button>
      )}
      <audio ref={audioRef} src="/music/originaldedodedo.wav" />
    </div>
  );
}

export default App;
